public class Sport {    private String title;    private String info;    private int imageResource;    public Sport(String title, String info ){        this.title = title;        this.info = info;    }    public Sport(String title, String info, int imgRes ){        this.title = title;        this.info = info;        imageResource = imgRes;    }    public String getTitle(){        return title;    }    public String getInfo(){        return info;    }    public int getImageId(){        return imageResource;    }}